    var hfSiteUrl = document.getElementById('hfSiteURL');
    var siteUrl = hfSiteUrl.value;
    var subdomainIndex = siteUrl.indexOf('/');

    hfSiteUrl.value = location.host + (subdomainIndex >= 0 ? siteUrl.substring(subdomainIndex) : '');
